using System.Collections;using System.Collections.Generic;using UnityEngine;public class Flammable : Elemental{    public float burnTime;    public Sprite onFire;    public Sprite frozen;    public PhysicsMaterial2D frozenMaterial;    private SpriteRenderer sr;    private IEnumerator burnRoutine;    private Sprite originalSprite;    private Collider2D col;    private PhysicsMaterial2D originalMaterial;    private Rigidbody2D rb2d;    private string curElement;    void Awake()    {        sr = GetComponent<SpriteRenderer>();        originalSprite = sr.sprite;        col = GetComponent<Collider2D>();        originalMaterial = col.sharedMaterial;        rb2d = GetComponent<Rigidbody2D>();    }    public override void Ignite()    {
        SoundController.PlayWhoosh();

        if (curElement == Element.ice)        {            curElement = null;            sr.sprite = originalSprite;            col.sharedMaterial = originalMaterial;            col.enabled = false;            col.enabled = true;            rb2d.mass = 300;        }        else if (curElement == null)        {            curElement = Element.fire;            sr.sprite = onFire;            col.sharedMaterial = originalMaterial;            col.enabled = false;            col.enabled = true;            burnRoutine = BurnRoutine(burnTime);            StartCoroutine(burnRoutine);        }    }    private IEnumerator BurnRoutine(float burnTime)    {        yield return new WaitForSeconds(burnTime);        Destroy(gameObject);    }    public override void Freeze()    {
        SoundController.PlayFreeze();

        if (curElement == Element.fire)        {            curElement = null;            StopCoroutine(burnRoutine);            sr.sprite = originalSprite;        }        else if (curElement == null)        {            curElement = Element.ice;            sr.sprite = frozen;            col.sharedMaterial = frozenMaterial;            col.enabled = false;            col.enabled = true;            rb2d.mass = 30;        }    }}